/**
 * 判断一个值是否是数组
 * @param {*} val
 * @returns {boolean}
 * @function isArray
 */
export function isArray(val: any): boolean

/**
 * 随机打乱一个数组
 * @function shuffleArray
 * @param {Array} array  待打乱数组
 * @return {Array}       已经打乱的数组
 * @example
 * shuffleArray([1, 2, 3, 4, 5, 6, 7, 8, 9])
 */
export function shuffleArray(array: [], size: number): []
